import { EndpointExample } from "@fern-api/openapi-ir-sdk";
import { RawSchemas } from "@fern-api/yaml-schema";

// this means the example only includes code samples and should be autogenerated
export function hasIncompleteExample(example: EndpointExample): boolean {
    if (example.type === "full") {
        return (
            example.request == null &&
            example.response == null &&
            example.queryParameters == null &&
            example.pathParameters == null &&
            example.headers == null
        );
    } else {
        const castExample = example.value as RawSchemas.ExampleEndpointCallSchema;
        return (
            castExample.request == null &&
            castExample.response == null &&
            castExample["query-parameters"] == null &&
            castExample["path-parameters"] == null &&
            castExample.headers == null
        );
    }
}
