import (
	context "context"
	fern "github.com/oauth-client-credentials/fern"
	fernclient "github.com/oauth-client-credentials/fern/client"
)

client := fernclient.NewClient()
response, err := client.Auth.GetTokenWithClientCredentials(
	context.TODO(),
	&fern.GetTokenRequest{
		ClientId:     "string",
		ClientSecret: "string",
	},
)

import (
	context "context"
	fern "github.com/oauth-client-credentials/fern"
	fernclient "github.com/oauth-client-credentials/fern/client"
)

client := fernclient.NewClient()
response, err := client.Auth.RefreshToken(
	context.TODO(),
	&fern.RefreshTokenRequest{
		ClientId:     "string",
		ClientSecret: "string",
		RefreshToken: "string",
	},
)
